// Chorus Lapilli

import { useState } from 'react';

function Square({value ,onSquareClick}) {
  return <button className="square" onClick = {onSquareClick}>{value}</button>;
}

export default function Board() {
  const [xIsNext, setXIsNext] = useState(true);
  const [squares, setSquares] = useState(Array(9).fill(null));
  const [xCount, setXCount] = useState(0); //creating states for the variables xCount and oCount to count how many Xs and Os on the b
  const [oCount, setOCount] = useState(0); //need to create states because the variables 
  const [moveSecond, setMoveSecond] = useState(null); //checks to see if on second move
  const [prevXIndex, setprevXIndex] = useState(0); //keeps track of X's first moves
  const [prevOIndex, setprevOIndex] = useState(0); //keeps track of Y's first moves

  //NEED TO IMPLEMENT after the first click, going onto second before it is the next players turn  --> that is need to let them go twice

  function handleClick(i) {

    // if (squares[i] || calculateWinner(squares)) { //returns early if there is something on that square 
    //   return;
    // }

    if (calculateWinner(squares)) { //returns early if there is something on that square 
        return;
    }

    const nextSquares = squares.slice(); //copy of actuals squares array

    if (xIsNext) { //x's turn
      if (xCount == 2 && moveSecond == null){ //on first move
        setprevXIndex(i)
        setMoveSecond(true)
        setXCount(3) //only 2 pieces now
      }
      else if(xCount == 3 && moveSecond){ //second move
          let validity = isValidMove(prevXIndex,i,squares) //check to see if the second move is valid
          if (centerSpot(prevXIndex,squares,xCount)){  //can win the center spot
            nextSquares[i] = "X" //marking new location
            setSquares(nextSquares) //set to the new board
            setXCount(2) //2 pieces again
            setMoveSecond(null)
          }
          if (validity){   //second move is valid ; also removes the center piece if center can win
            nextSquares[prevXIndex] = null //reset the previous position 
            nextSquares[i] = "X" //marking new location
            setMoveSecond(null) //no longer in second move, reset
            setXIsNext(!xIsNext) //next person's turn
            setSquares(nextSquares); //set to the new board
            setXCount(2) //2 pieces again
          }        
          else{return;} //choose another move that is valid
      }
      else{ //has not reached 3 Xs --> only handle one click
        // if (xCount < 3){
        nextSquares[i] = "X"
        setXCount(xCount + 1) 
        setSquares(nextSquares); //set to new board
        setXIsNext(!xIsNext); //change the turn
        // }
      }
    } else { //O's turn 

      if (oCount == 2 && moveSecond == null){ //on first move
        setprevOIndex(i)
        setMoveSecond(true)
        setOCount(3)
      }
      else if(oCount == 3 && moveSecond){ //second move
          let validity = isValidMove(prevOIndex,i,squares) //check to see if the second move is valid
          if (centerSpot(prevOIndex,squares,oCount)){  //can win the center spot
            nextSquares[i] = "O" //marking new location
            setSquares(nextSquares); //set to the new board
            setOCount(2)
            setMoveSecond(null)
          }
          else if (validity){   //second move is valid ; also removes the center piece if center can win
            nextSquares[prevOIndex] = null //reset the previous position 
            nextSquares[i] = "O" //marking new location
            setMoveSecond(null) //no longer in second move, reset
            setXIsNext(!xIsNext) //next person's turn
            setSquares(nextSquares); //set to the new board
            setOCount(2)
          }        
          else{return;} //choose another move that is valid
      }
      else{ //has not reached 3 Xs --> only handle one click
        // if (oCount < 3){
        nextSquares[i] = "O"
        setOCount(oCount + 1) 
        setSquares(nextSquares); //set to new board
        setXIsNext(!xIsNext); //change the turn
        // }
      }
    }
  } //end of handleClick

  const winner = calculateWinner(squares); 
  let status; 
  if (winner) {
    status = "Winner: " + winner;
  } 
  else {
    status = "Next player: " + (xIsNext ? "X" : "O"); //if xIsNext , next player is X, else it is O
  }

  return (
    <>
      <div className="status">{status}</div>
      <div className="board-row">
        <Square value={squares[0]} onSquareClick={() => handleClick(0)} />
        <Square value={squares[1]} onSquareClick={() => handleClick(1)} />
        <Square value={squares[2]} onSquareClick={() => handleClick(2)} />
      </div>
      <div className="board-row">
        <Square value={squares[3]} onSquareClick={() => handleClick(3)} />
        <Square value={squares[4]} onSquareClick={() => handleClick(4)} />
        <Square value={squares[5]} onSquareClick={() => handleClick(5)} />
      </div>
      <div className="board-row">
        <Square value={squares[6]} onSquareClick={() => handleClick(6)} />
        <Square value={squares[7]} onSquareClick={() => handleClick(7)} />
        <Square value={squares[8]} onSquareClick={() => handleClick(8)} />
      </div>
    </>
  );
}

function isValidMove (currIndex,moveIndex,squares){ //checks to see if the second move is a valid move --> call this function on the second click
  const possibleMoves ={ // 0:,1:....8: are the keys --> possible moves are the indexs in the list 
    0:[1,3,4],
    1:[0,2,3,4,5],
    2:[1,4,5],
    3:[0,1,4,6,7],
    4:[0,1,2,3,5,6,7,8],
    5:[1,2,4,7,8],
    6:[3,4,7],
    7:[3,4,5,6,8],
    8:[4,5,7]
  }; 
  let check = possibleMoves[currIndex].includes(moveIndex); //finds the key of curr index, then checks to see if the index they want to move to is a valid box from that curr index
  if (check){ //check to see if the moveIndex is available 
    if (squares[moveIndex] == null){
      return true
    }
    else{
      return false
    }
  }
  else{ //return false because the square they chose is not valid 
    return false
  }
}

function centerSpot (centIndex,squares,pieces){
  if (centIndex === 4 && pieces == 2){
    const centerWin = [
      [3, 4, 5],  //middle row
      [1, 4, 7],  //middle column
      [0, 4, 8],  //diagonal (top-left to bottom-right)
      [2, 4, 6]   //diagonal (top-right to bottom-left)
    ]

    for (let j = 0; j < centerWin.length; j++){
      const [a, b, c] = centerWin[j];
      if (squares[a] && squares[a] === squares[b] && squares[c] === null) { //checks if a space is open, note b is the center 
        return true;
      }
      else if (squares[a] === null && squares[b] === squares[c] && squares[c]) { //checks if a space is open, note b is the center
        return true;
      } 
    }
    return false; 
  }
  return false;
}

function calculateWinner (squares) {
  const lines = [
    [0, 1, 2],  //top row
    [3, 4, 5],  //middle row
    [6, 7, 8],  //bottom row
    [0, 3, 6],  //left column
    [1, 4, 7],  //middle column
    [2, 5, 8],  //right column
    [0, 4, 8],  //diagonal (top-left to bottom-right)
    [2, 4, 6]   //diagonal (top-right to bottom-left)
  ]; //refers to the possibilities of where a winner can win

  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) { //checks is all Xs or all Os
      return squares[a];
    }
  }
  return null;
}
